def generateRandomUsername():
    """Generates a random user name of the form ADJ+NOUN

    Returns:
        [string]: [username]
    """
    a = random.choice(ADJECTIVES).lower()
    n = random.choice(NOUNS).lower()
    return a + n

def randomCharString(N):
    res = ''.join(random.choices(string.ascii_uppercase +
                             string.digits, k = N))